import styled from "styled-components";
export const Wrapper = styled.div`{
    background-color: yellow;
    display: flex;
    font-size:30px;
    width: 90vw;
    height: 5em; 
    margin: 20px;
    font-size: 22px;
    border: 1px solid grey;
    padding: 10px;
    flex-direction: column;
    border-radius: 20px;
}`
export const ContentBox = styled.div`{
    display: flex;
    flex-direction: row;
    align-items: center;
}`
export const Content = styled.div`{
    font-size: 26px;
    padding: 10px;
    font-weight: bold;
    color: red;
}`
